import React, { useState, useEffect } from "react";
import axios from "axios";
import Karakter from "./components/Karakter";

const App = () => {
  const [data, setData] = useState([]);
  const [expansion, setExpension] = useState(false);
  const [searchTerm, serSearchTerm] = useState("");

  useEffect(() => {
    axios
      .get("https://swapi.dev/api/people/")
      .then((r) => {
        setData(r.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  function handleVisibilty() {
    expansion === false ? setExpension(true) : setExpension(false);
  }

  return (
    <div className="App">
      <h1 className="Header">Star Wars</h1>
    </div>
  );
};

export default App;
